mapper.select:
select supplier_mailbox,supplier_addr,supplier_name,supplier_id 
from Supplier 

     and supplier_id = #{supplierId}
     and supplier_name = #{supplierName}
     and supplier_addr = #{supplierAddr}
     and supplier_mailbox = #{supplierMailbox}

select supplier_mailbox,supplier_addr,supplier_name,supplier_id 
from Supplier 

     and supplier_id = #{Supplier.supplierId}
     and supplier_name = #{Supplier.supplierName}
     and supplier_addr = #{Supplier.supplierAddr}
     and supplier_mailbox = #{Supplier.supplierMailbox} 

limit #{startIndex},#{pageSize};

select supplier_mailbox,supplier_addr,supplier_name,supplier_id from Supplier
and supplier_id like concat('%',#{Supplier.supplierId},'%') and supplier_name like concat('%',#{Supplier.supplierName},'%') and supplier_addr like concat('%',#{Supplier.supplierAddr},'%') and supplier_mailbox like concat('%',#{Supplier.supplierMailbox},'%')
limit #{startIndex},#{pageSize};

select count(1) from Supplier 

     and supplier_id = #{supplierId}
     and supplier_name = #{supplierName}
     and supplier_addr = #{supplierAddr}
     and supplier_mailbox = #{supplierMailbox}

mapper.update:
update Supplier
supplier_name = #{supplierName}, supplier_addr = #{supplierAddr}, supplier_mailbox = #{supplierMailbox},
where supplier_id = #{supplierId}

update Supplier 

    supplier_name = #{SetValue.supplierName},
    supplier_addr = #{SetValue.supplierAddr},
    supplier_mailbox = #{SetValue.supplierMailbox},



         and supplier_id = #{Condition.supplierId}
         and supplier_name = #{Condition.supplierName}
         and supplier_addr = #{Condition.supplierAddr}
         and supplier_mailbox = #{Condition.supplierMailbox}

mapper.delete:
delete from Supplier 

     1 = 2
     and supplier_id = #{supplierId}
     and supplier_name = #{supplierName}
     and supplier_addr = #{supplierAddr}
     and supplier_mailbox = #{supplierMailbox}
