mapper.select:
select warehouse_name, warehouse_maxinventory, warehouse_id
from Warehouse
where warehouse_id = #{warehouseId};

select warehouse_name,warehouse_maxinventory,warehouse_id
from Warehouse

    and warehouse_id = #{warehouseId}
    and warehouse_name = #{warehouseName}
    and warehouse_maxinventory = #{warehouseMaxinventory}

select warehouse_name,warehouse_maxinventory,warehouse_id
from Warehouse

    and warehouse_id = #{Warehouse.warehouseId}
    and warehouse_name = #{Warehouse.warehouseName}
    and warehouse_maxinventory =
        #{Warehouse.warehouseMaxinventory}


limit #{startIndex},#{pageSize};

select count(1) from Warehouse

    and warehouse_id = #{warehouseId}
    and warehouse_name = #{warehouseName}
    and warehouse_maxinventory = #{warehouseMaxinventory}

mapper.update:
update Warehouse
warehouse_name = #{warehouseName}, warehouse_maxinventory = #{warehouseMaxinventory},
where warehouse_id = #{warehouseId}

update Warehouse

    warehouse_name = #{SetValue.warehouseName},
    warehouse_maxinventory =
        #{SetValue.warehouseMaxinventory},




        and warehouse_id = #{Condition.warehouseId}
        and warehouse_name = #{Condition.warehouseName}
        and warehouse_maxinventory =
            #{Condition.warehouseMaxinventory}

mapper.delete:
delete from Warehouse

    1 = 2
    and warehouse_id = #{warehouseId}
    and warehouse_name = #{warehouseName}
    and warehouse_maxinventory = #{warehouseMaxinventory}
